import React from 'react';
import '../App.css';

const Search = () => {
  const [type, setType] = React.useState('name');
  const [name, setName] = React.useState('');
  const [id, setId] = React.useState('');
  const [start, setStart] = React.useState(1938)
  const [end, setEnd] = React.useState(2023)

  const submit = () => {
    let key = '';
    if (type === 'name') {
      key = name;
    } else {
      key = id;
    }
    if (key === '') {
      alert('Alert: ' + type + ' cannot be empty!');
    } else {
      console.log(key);
    }
  }

  return (
    <div className='main'>
      <div className='typeSelect'> 
        <input
          type='checkbox'
          className='checkbox'
          checked={type==='id'}
          style={{ display:'inline' }}
          onChange={() => {
            if (type === 'name') {
              setType('id');
            } else {
              setType('name')
            }
          }}
        />
        <span className='text'>Search by ID | </span>
        <input
          type='checkbox'
          className='checkbox'
          checked={type==='name'}
          style={{ display:'inline' }}
          onChange={() => {
            if (type === 'name') {
              setType('id');
            } else {
              setType('name')
            }
          }}
        />
        <span className='text'>Search by Name</span>
      </div>
      {type==='name' && <input
        type='text'
        value={name}
        className='Input'
        placeholder='Name of the researcher'
        onChange={e => setName(e.target.value)}
      />}
      {type==='id' && <input
        type='text'
        value={id}
        className='Input'
        placeholder='ID of the researcher'
        onChange={e => setId(e.target.value)}
      />}
      <div className='yearInput'>
        From: <input
          type='number'
          className='year'
          value={start}
          min={1938}
          max={end}
          onChange={e => setStart(e.value)}
        /> To: <input
          type='number'
          className='year'
          value={end}
          min={start}
          max={2023}
          onChange={e => setEnd(e.value)}
        />
      </div>
      <button
        onClick={() => submit()}
      >Let's Start!</button>
    </div>
  )
}

export default Search
